package yabo;

public class rowsAndColumnsStar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int row = 0; row < 5; row++) {
			  for (int column = 0; column <= row; column++) {
			    System.out.print("*");
			  }
			  System.out.println();	
			  }
		 
		
	}
}
